const menuSpan=document.querySelectorAll(".header-mobile > span"),menuList=document.querySelector(".header-menu"),menuButton=document.querySelector(".header-mobile");menuButton.addEventListener("click",(function(){if(menuList.classList.contains("active")){for(let e=0;e<menuSpan.length;e++)menuSpan[e].classList.remove("active");menuList.classList.remove("active")}else{for(let e=0;e<menuSpan.length;e++)menuSpan[e].classList.add("active");menuList.classList.add("active")}}));